<?php/*		Clase de métodos de inserción, modificación, eliminación y consulta sobre la tabla carreras * * @author Nelson D. Garzón M.	*/@session_start();include_once(dirname(__FILE__) . '/../data.bases/connection.mysql.class.php');include_once(dirname(__FILE__) . '/audit.dao.class.php');class careers {    private $conn;	private $audit;        function careers()    {        $this->conn = new connection();		$this->audit = new audit();            }	function get_all_count_careers()    {        $record_set = $this->conn->db->Execute('SELECT COUNT(*) FROM carrera');        if($record_set != false)        {            return $record_set;        }  else {            return null;        }    }	    function get_all_careers($order, $index, $page)    {        $record_set = $this->conn->db->Execute('SELECT * FROM carrera ORDER BY '.$order.' LIMIT '.$index.','.$page);        if($record_set != false)        {            return $record_set;        }  else {            return null;        }    }		function get_all_careers_list()    {        $record_set = $this->conn->db->Execute('SELECT id, nombre FROM carrera ORDER BY nombre ASC ');        if($record_set != false)        {            return $record_set;        }  else {            return null;        }    }		function get_all_careers_list_by_user($user)    {        $record_set = $this->conn->db->Execute('SELECT DISTINCT c.id, c.nombre FROM usuario_carrera uc INNER JOIN carrera c ON uc.carrera = c.id WHERE uc.usuario = ? ORDER BY c.nombre ASC ', array($user));        if($record_set != false)        {            return $record_set;        }  else {            return null;        }    }		function get_career($id)    {		$record_set = $this->conn->db->Execute('SELECT * FROM carrera WHERE sigla = ?', array($id));			        if($record_set != false)        {            return $record_set;        }  else {            return null;        }     }        function get_career_search($name = "", $order, $index, $page)    {		if(!empty($name)){			$record_set = $this->conn->db->Execute('SELECT * FROM carrera WHERE CONCAT(sigla," ",nombre) LIKE ? ORDER BY '.$order.' LIMIT '.$index.','.$page, array("%".$name."%"));		}else{			$record_set = $this->conn->db->Execute('SELECT * FROM carrera ORDER BY '.$order.' LIMIT '.$index.','.$page);		}        if($record_set != false)        {            return $record_set;        }  else {            return null;        }     }       function insert_career($initials, $name, $description)    {		$this->audit->register_audit(1, 'INSERT INTO carrera(sigla, nombre, descripcion) VALUES(?,?,?)', $_SESSION['id']);        return $this->conn->db->Execute('INSERT INTO carrera(sigla, nombre, descripcion) VALUES(?,?,?) ', 			   array($initials, $name, $description));    }		function update_career($id, $initials, $name, $description)    {		$this->audit->register_audit(2, 'UPDATE carrera SET sigla = ?, nombre = ?, descripcion = ? WHERE id = ?', $_SESSION['id']);        return $this->conn->db->Execute('UPDATE carrera SET sigla = ?, nombre = ?, descripcion = ? WHERE id = ? ', 			   array($initials, $name, $description, $id));    }        function delete_career($id)    {		$this->audit->register_audit(3, 'DELETE FROM carrera WHERE id = ?', $_SESSION['id']);        return $this->conn->db->Execute('DELETE FROM carrera WHERE id = ?', array($id));    }}	?>