<?php/*		Clase de métodos de inserción, modificación, eliminación y consulta sobre la tabla carreras * * @author Nelson D. Garzón M.	*/@session_start();include_once(dirname(__FILE__) . '/../data.bases/connection.mysql.class.php');include_once(dirname(__FILE__) . '/audit.dao.class.php');class states {    private $conn;	private $audit;        function states()    {        $this->conn = new connection();  		$this->audit = new audit();		    }	function get_all_count_states()    {        $record_set = $this->conn->db->Execute('SELECT COUNT(*) FROM estado');        if($record_set != false)        {            return $record_set;        }  else {            return null;        }    }	    function get_all_states($order, $index, $page)    {        $record_set = $this->conn->db->Execute('SELECT * FROM estado ORDER BY '.$order.' LIMIT '.$index.','.$page);        if($record_set != false)        {            return $record_set;        }  else {            return null;        }    }		function get_state($name)    {		$record_set = $this->conn->db->Execute('SELECT * FROM estado WHERE nombre = ?', array($name));			        if($record_set != false)        {            return $record_set;        }  else {            return null;        }     }        function get_state_search($name = "", $order, $index, $page)    {		if(!empty($name)){			$record_set = $this->conn->db->Execute('SELECT * FROM estado WHERE nombre LIKE ? ORDER BY '.$order.' LIMIT '.$index.','.$page, array("%".$name."%"));		}else{			$record_set = $this->conn->db->Execute('SELECT * FROM estado ORDER BY '.$order.' LIMIT '.$index.','.$page);		}        if($record_set != false)        {            return $record_set;        }  else {            return null;        }     }       function insert_state($name)    {		$this->audit->register_audit(1, 'INSERT INTO estado(nombre) VALUES(?)', $_SESSION['id']);        return $this->conn->db->Execute('INSERT INTO estado(nombre) VALUES(?) ', 			   array($name));    }		function update_state($id, $name)    {		$this->audit->register_audit(2, 'UPDATE estado SET nombre = ? WHERE id = ?', $_SESSION['id']);        return $this->conn->db->Execute('UPDATE estado SET nombre = ? WHERE id = ? ', 			   array($name, $id));    }        function delete_state($id)    {		$this->audit->register_audit(3, 'DELETE FROM estado WHERE id = ?', $_SESSION['id']);        return $this->conn->db->Execute('DELETE FROM estado WHERE id = ?', array($id));    }}	?>