<?php/*		Clase de métodos de inserción, modificación, eliminación y consulta sobre la tabla carreras * * @author Nelson D. Garzón M.	*/@session_start();include_once(dirname(__FILE__) . '/../data.bases/connection.mysql.class.php');include_once(dirname(__FILE__) . '/audit.dao.class.php');class type_notes {    private $conn;	private $audit;        function type_notes()    {        $this->conn = new connection();  		$this->audit = new audit();    }	function get_all_count_type_notes()    {        $record_set = $this->conn->db->Execute('SELECT COUNT(*) FROM tipo_nota');        if($record_set != false)        {            return $record_set;        }  else {            return null;        }    }	    function get_all_type_notes($order, $index, $page)    {        $record_set = $this->conn->db->Execute('SELECT * FROM tipo_nota ORDER BY '.$order.' LIMIT '.$index.','.$page);        if($record_set != false)        {            return $record_set;        }  else {            return null;        }    }		function get_all_type_notes_list()    {        $record_set = $this->conn->db->Execute('SELECT * FROM tipo_nota ORDER BY id');        if($record_set != false)        {            return $record_set;        }  else {            return null;        }    }		function get_type_note($name)    {		$record_set = $this->conn->db->Execute('SELECT * FROM tipo_nota WHERE nombre = ?', array($name));			        if($record_set != false)        {            return $record_set;        }  else {            return null;        }     }        function get_type_note_search($name = "", $order, $index, $page)    {		if(!empty($name)){			$record_set = $this->conn->db->Execute('SELECT * FROM tipo_nota WHERE nombre LIKE ? ORDER BY '.$order.' LIMIT '.$index.','.$page, array("%".$name."%"));		}else{			$record_set = $this->conn->db->Execute('SELECT * FROM tipo_nota ORDER BY '.$order.' LIMIT '.$index.','.$page);		}        if($record_set != false)        {            return $record_set;        }  else {            return null;        }     }       function insert_type_note($name)    {		$this->audit->register_audit(1, 'INSERT INTO tipo_nota(nombre) VALUES(?)', $_SESSION['id']);        return $this->conn->db->Execute('INSERT INTO tipo_nota(nombre) VALUES(?) ', 			   array($name));    }		function update_type_note($id, $name)    {		$this->audit->register_audit(2, 'UPDATE tipo_nota SET nombre = ? WHERE id = ? ', $_SESSION['id']);        return $this->conn->db->Execute('UPDATE tipo_nota SET nombre = ? WHERE id = ? ', 			   array($name, $id));    }        function delete_type_note($id)    {		$this->audit->register_audit(3, 'DELETE FROM tipo_nota WHERE id = ?', $_SESSION['id']);        return $this->conn->db->Execute('DELETE FROM tipo_nota WHERE id = ?', array($id));    }}	?>